mapper.select:
select su.user_id as id, su.user_name as username, su.user_pass as userpass, su.role_id as roleId, su.parent_id as parentid, su.user_status as userstatus, su.user_remark as userremark, su.create_name as createName, su.create_time as createTime, su.update_name as updateName, su.update_time as updateTime, su.del_status as delStatus FROM sys_user as su where 1=1 and del_status=1
and su.user_name like CONCAT('%','${userName}','%')
and su.parent_id =#{parentId}
and su.e_status =#{eStatus}
limit #{lowerLimit},#{upperLimit}

select su.user_id as id, su.user_name as username, su.user_pass as userpass, su.role_id as roleId, su.parent_id as parentid, su.user_status as userstatus, su.user_remark as userremark, su.create_name as createName, su.create_time as createTime, su.update_name as updateName, su.update_time as updateTime, su.del_status as delStatus FROM sys_user as su where 1=1 and su.user_status =1 and del_status=1
and su.user_name like CONCAT('%','${userName}','%')
and su.parent_id =#{parentId}
and su.e_status =#{eStatus}
limit #{lowerLimit},#{upperLimit}

select count(*) from sys_user where 1=1  and del_status=1

	and user_name like CONCAT('%','${userName}','%')


	and role_id = #{roleId}


	and parent_id =#{parentId}


	and e_status =#{eStatus}

select count(*) from sys_user where 1=1 and user_status=1  and del_status=1

	and user_name like CONCAT('%','${userName}','%')


	and role_id = #{roleId}


	and parent_id =#{parentId}


	and e_status =#{eStatus}

mapper.update:
update sys_user set
	e_status =1
where user_id=#{uid}
